import { exportComponentAsJPEG, exportComponentAsPDF, exportComponentAsPNG } from "react-component-export-image";
import React from 'react'
import './main.css'

class MemeGenerator extends React.Component {
	constructor(props) {
		super(props)

		this.state = {
			topText: "",
            bottomText: "",
            randomImg: "http://i.imgflip.com/1bij.jpg",
            allMemeImgs: [],
		}
	}

	componentDidMount() {
        fetch("https://api.imgflip.com/get_memes")
        .then(response => response.json())
        .then(response => {
            const {memes} = response.data
            this.setState({ allMemeImgs: memes })
        })
    }

    handleChange = (event) => {
        const {name, value} = event.target
        this.setState({
            [name]: value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault()
        const randNum = Math.floor(Math.random() * this.state.allMemeImgs.length)
        const randMemeImg = this.state.allMemeImgs[randNum].url
        this.setState({ randomImg: randMemeImg })
    }

	render() {
		return (
			<div style = {{textAlign: "center"}}>
                <form className = "meme-form" onSubmit = {this.handleSubmit}>
                    <input 
                        type = "text"
                        name = "topText"
                        placeholder = "Top Text"
                        value = {this.state.topText}
                        onChange = {this.handleChange}

                    />
                    <input 
                        type = "text"
                        name = "bottomText"
                        placeholder = "Bottom Text"
                        value = {this.state.bottomText}
                        onChange = {this.handleChange}
                    />
                    <button>Next Image</button>
                </form>
                <div className = "meme">
                    <img alt = "memeImage" src = {this.state.randomImg} />
                    <h2 className = "top">{this.state.topText}</h2>
                    <h2 className = "bottom">{this.state.bottomText}</h2>
                </div>
			</div>
		)
	}
}

class MyComponent extends React.Component {
    constructor(props) {
      super(props);
      this.componentRef = React.createRef();
    }

    render() {
      return (
      <React.Fragment>
          <MemeGenerator ref={this.componentRef} />
          <button className="jpeg" onClick={() => exportComponentAsJPEG(this.componentRef)}>
              Export As JPEG
          </button>
          <button className="png" onClick={() => exportComponentAsPNG(this.componentRef)}>
              Export As PNG
          </button>
      </React.Fragment>);
    }
   }

export default MyComponent;
